function higherPrime(n) {
  count = 0;
  for (i = 1; i <= n; i++) {
    if (n % i == 0) {
      count++;
    }
  }
  if (count == 2) {
    return n;
  } else {
    return higherPrime(n + 1);
  }
}

console.log(higherPrime(7)); // 7
console.log(higherPrime(14)); // 17

