function powerOf2(n) {
  if (n % 2 == 0) {
    for (i = 1; i <= n; i++) {
      if (n == 2 ** i) {
        return true;
      }
    }
    return false;
  } else return false;
}

console.log(powerOf2(16)); // true
console.log(powerOf2(10)); // false

