function revStr(str) {
  let stack = [];
  let result = '';

  // turn string into stack
  for (i = 0; i < str.length; i++) {
    stack[i] = str[i];
  }

  let length = stack.length;

  for (i = length - 1; i >= 0; i--) {
    // add every i'th element to result
    result = result + stack[i];

    // pop last element
    length = i;
  }

  return console.log(result);
}

